//# open links in new tab
$('a').each(function(){var a=new RegExp('/'+window.location.host+'/');if(!a.test(this.href)){$(this).attr("target","_blank")}});

//# show language button if different language versions exist

if ( $( "#EN" ).length ) {
    $( ".language-nav" ).css( 'display' , 'grid' );
}

//# scroll button & language button

const scrollTop = document.getElementById('scrolltop')
const language = document.getElementById('language')

window.onload = () => {
    scrollTop.style.visibility = "hidden";
    scrollTop.style.opacity = 0;
    language.style.visibility = "hidden";
    language.style.opacity = 0;
}

if ( $(window).width() > 991) {
    window.onscroll = () => {
        if (window.scrollY > 755) {
            scrollTop.style.visibility = "visible";
            scrollTop.style.opacity = 0.9;
            language.style.visibility = "visible";
            language.style.opacity = 1;
        } else {
            scrollTop.style.visibility = "hidden";
            scrollTop.style.opacity = 0;
            language.style.visibility = "hidden";
            language.style.opacity = 0;
        }
    };
}

if ( $(window).width() <= 991) {
    window.onscroll = () => {
        if (window.scrollY > 555) {
            scrollTop.style.visibility = "visible";
            scrollTop.style.opacity = 1;
            language.style.visibility = "visible";
            language.style.opacity = 1;
        } else {
            scrollTop.style.visibility = "hidden";
            scrollTop.style.opacity = 0;
            language.style.visibility = "hidden";
            language.style.opacity = 0;
        }
    };
}

if ( $(window).width() <= 701) {
    $('.gh-burger').click(function(){
        $(this).toggleClass('white');
        $('.gh-head-logo').toggleClass('invert no_invert');
        $('.gh-head, .gh-head-actions').toggleClass('accent no_accent');

    });
    $('.nav').click(function(){
        $('.gh-head-logo').toggleClass('invert no_invert');
        $('.gh-head, .gh-head-actions').toggleClass('accent no_accent');
    });
}